import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate from react-router-dom
import Heading from "../../common/heading/Heading";
import "./Hero.css";

const Hero = () => {
  const navigate = useNavigate(); // Initialize useNavigate
  const [isAppointmentOpen, setIsAppointmentOpen] = useState(false); // State to manage appointment form visibility

  const handleViewCourseClick = () => {
    navigate('/allcourses'); // Navigate to the All Courses page
  };

  const handleGetStartedClick = () => {
    setIsAppointmentOpen(true); // Show the appointment form
  };

  const closeAppointmentForm = () => {
    setIsAppointmentOpen(false); // Close the appointment form
  };

  return (
    <>
      <section className='hero'>
        <div className='container'>
          <div className='row'>
            <Heading subtitle='WELCOME TO ACADEMIA' title='Best Online Education Expertise' />
            <p>Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts.</p>
            <div className='button'>
              <button className='primary-btn' onClick={handleGetStartedClick}>
                GET STARTED NOW <i className='fa fa-long-arrow-alt-right'></i>
              </button>
              <button onClick={handleViewCourseClick}>
                VIEW COURSE <i className='fa fa-long-arrow-alt-right'></i>
              </button>
            </div>
          </div>
        </div>
      </section>
      <div className='margin'></div>

      {/* Appointment Form Pop-up */}
      {isAppointmentOpen && (
        <div className='popup'>
          <div className='popup-content'>
            <span className='close' onClick={closeAppointmentForm}>&times;</span>
            <h2>Schedule an Appointment</h2>
            <form>
              <label>Name:</label>
              <input type='text' required />
              <label>Email:</label>
              <input type='email' required />
              <label>Date:</label>
              <input type='date' required />
              <label>Time:</label>
              <input type='time' required />
              <button type='submit'>Submit</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
}

export default Hero;
